#!/bin/bash

sudo apt update
sudo apt install build-essential cmake libeigen3-dev zlib1g-dev unzip
git clone https://github.com/aloysiogl/pi_inf442.git
cd pi_inf442/

mkdir build
cd build
cmake ..
make
cd ../bin

# ======== ENGLISH DS ========
wget "https://www.enseignement.polytechnique.fr/informatique/INF442/PI/data.zip"
unzip data.zip
rm data.zip
rm -r __MACOSX
cd data

